sys_script_include.script:
var Events = Class.create();
Events.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	user_event_exists: function(user_id, event) {
		user_id = this.getParameter("p_user_id") || user_id;
		event = this.getParameter("p_event") || event;
		var result = Events.userEventExists(user_id, event);

		return JSON.stringify({
			result: result
		}, null, 2);
	},

    type: 'Events'
});


(function(self) {

	self.tables = {
		event: "x_650610_basicatt_attendance"
	};


    self.userEventExists = function(user_id, event) {
        var gr_lookup = new GlideRecord(self.tables.event);
        gr_lookup.addQuery('user', user_id);
        gr_lookup.addQuery('event', event);
        gr_lookup.query();

        if (gr_lookup.getRowCount() > 0) {
            return true;
        } else {
			return false;
		}
    };

})(Events);
